function [a, b, c,pred] = duane(x,y,a,b,c)  sp=0.000479  sp2=0.000000000008  sp3=0.0000000001  for i=1:5000    pred=a*(1-((b/(b+x))^c));    dela=(1-((b/(b+x))^c));    delb=(-c*a*(b.^(c-1))./((b+x).^(c+1));    delc=(-a*(b/(b+x)))*(log(b/(b+x)));    era=sum((pred-y).*dela);    erb=sum((pred-y).*delb);    erc=sum((pred-y).*delc);    a=a-(sp*era);    b=b-(sp2*erb);    c=c-(sp3*erc);  endendfunction