function [a, b, c,pred] = gg(x, y, a, b,c)  sp=0.000001  sp2=0.000000000008  sp3=0.0000000001  for i=1:5000    pred=a*(1-(e.^((-b*(x.^c)))));    dela=1-(e.^(-b*(x.^c)));    delb=a*e.^(-b*(x.^c));    delc=a*b*e.^(-b*(x.^c)).*log(x);    era=sum((pred-y).*dela);    erb=sum((pred-y).*delb);    erc=sum((pred-y).*delc);    a=a-(sp*era);    b=b-(sp2*erb);    c=c-(sp3*erc);  endendfunction