function [a, b, k, pred] = lg(x, y, a, b, k)  sp=0.0009  sp2=0.000000012  sp3=0.00000088  for i=1:5000    pred=a./(1+k*(e.^(-b*x)));    dela=1./(1+k*(e.^(-b*x)));    delb=(1*a./((1+k*e.^(-b*x)).^2)).*(e.^(-b*x));    delk=(a./((1+k*e.^(-b*x)).^2)).*(e.^(-b*x));    era=sum((pred-y).*dela);    erb=sum((pred-y).*delb);    erk=sum((pred-y).*delk);        a=a-(sp*era);    b=b-(sp2*erb);    k=k-(sp3*erk);      endendfunction