function [a, b, b1, pred] = ss(x, y, a, b,b1)  sp=0.0001  sp2=0.00000001  sp3=0.0000001  for i=1:5000    term=1+b1*(e.^-(b*x));    pred=(a*(1-(e.^(-b*x))))./(term);    dela=(1-(e.^(-b*x)))./term;    delb=(a*e.^(-b*x))./term;    delb1=((a*(1-(e.^(-b*x))))./((term).^2)).*(e.^-(b*x))    era=sum((pred-y).*dela);    erb=sum((pred-y).*delb);    erb1=sum((pred-y).*delb1);    a=a-(sp*era);    b=b-(sp2*erb);    b1=b1-(sp3*erb1);  endendfunction